6 667 668 669 670 671 672 673 674 675 676 677 678 679 680 681 682 683 684 685 686 687 688 689 690 691 692 693 694 695 696 697 698 699 700 701 702 703 704 705 706 707 708 709 710 711 712 713 714 715 716 717 718 719 720 721 722 723 724 725 726 727 728 729 730 731 732 733 734 735 736 737 738 739 740 741 742 743 744 745 746 747 748 749 class QuantumCircuit ( object ): def __init__ ( self , num : int ): """ Initialize a QuantumCircuit object Args: num (int): Total qubit number used """ self . num = num self . gates = [] self . openqasm = "" self . circuit = [] self . measures = dict ( zip ( range ( num ), range ( num ))) self . _used_qubits = [] @property def used_qubits ( self ) -> List : self . layered_circuit () return self . _used_qubits def add_gate ( self , gate : QuantumGate ): self . gates . append ( gate ) def layered_circuit ( self ) -> np . ndarray : """ Make layered circuit from the gate sequence self.gates. Returns: A layered list with left justed circuit. """ num = self . num gatelist = self . gates gateQlist = [[] for i in range ( num )] used_qubits = [] for gate in gatelist : if isinstance ( gate , SingleQubitGate ) or isinstance ( gate , Delay ): gateQlist [ gate . pos ] . append ( gate ) if gate . pos not in used_qubits : used_qubits . append ( gate . pos ) elif isinstance ( gate , Barrier ) or isinstance ( gate , MultiQubitGate ) or isinstance ( gate , XYResonance ): pos1 = min ( gate . pos ) pos2 = max ( gate . pos ) gateQlist [ pos1 ] . append ( gate ) for j in range ( pos1 + 1 , pos2 + 1 ): gateQlist [ j ] . append ( None ) if isinstance ( gate , MultiQubitGate ) or isinstance ( gate , XYResonance ): for pos in gate . pos : if pos not in used_qubits : used_qubits . append ( pos ) maxlayer = max ([ len ( gateQlist [ j ]) for j in range ( pos1 , pos2 + 1 )]) for j in range ( pos1 , pos2 + 1 ): layerj = len ( gateQlist [ j ]) pos = layerj - 1 if not layerj == maxlayer : for i in range ( abs ( layerj - maxlayer )): gateQlist [ j ] . insert ( pos , None ) maxdepth = max ([ len ( gateQlist [ i ]) for i in range ( num )]) for gates in gateQlist : gates . extend ([ None ] * ( maxdepth - len ( gates ))) for m in self . measures . keys (): if m not in used_qubits : used_qubits . append ( m ) used_qubits = np . sort ( used_qubits ) new_gateQlist = [] for old_qi in range ( len ( gateQlist )): gates = gateQlist [ old_qi ] if old_qi in used_qubits : new_gateQlist . append ( gates ) lc = np . array ( new_gateQlist ) lc = np . vstack (( used_qubits , lc . T )) . T self . circuit = lc self . _used_qubits = list ( used_qubits ) return self . circuit def draw_circuit ( self , width : int = 4 , return_str : bool = False ): """ Draw layered circuit using ASCII, print in terminal. Args: width (int): The width of each gate. return_str: Whether return the circuit string. """ self . layered_circuit () gateQlist = self . circuit num = gateQlist . shape [ 0 ] depth = gateQlist . shape [ 1 ] - 1 printlist = np . array ([[ "" ] * depth for i in range ( 2 * num )], dtype = "<U30" ) reduce_map = dict ( zip ( gateQlist [:, 0 ], range ( num ))) reduce_map_inv = dict ( zip ( range ( num ), gateQlist [:, 0 ])) for l in range ( depth ): layergates = gateQlist [:, l + 1 ] maxlen = 1 + width for i in range ( num ): gate = layergates [ i ] if isinstance ( gate , SingleQubitGate ) or isinstance ( gate , Delay ): printlist [ i * 2 , l ] = gate . symbol maxlen = max ( maxlen , len ( gate . symbol ) + width ) elif isinstance ( gate , MultiQubitGate ) or isinstance ( gate , XYResonance ): q1 = reduce_map [ min ( gate . pos )] q2 = reduce_map [ max ( gate . pos )] printlist [ 2 * q1 + 1 : 2 * q2 , l ] = "|" printlist [ q1 * 2 , l ] = "#" printlist [ q2 * 2 , l ] = "#" if isinstance ( gate , ControlledGate ): #Controlled-Multiqubit gate for ctrl in gate . ctrls : printlist [ reduce_map [ ctrl ] * 2 , l ] = "*" if gate . targ_name == "SWAP" : printlist [ reduce_map [ gate . targs [ 0 ]] * 2 , l ] = "x" printlist [ reduce_map [ gate . targs [ 1 ]] * 2 , l ] = "x" else : tq1 = reduce_map [ min ( gate . targs )] tq2 = reduce_map [ max ( gate . targs )] printlist [ tq1 * 2 , l ] = "#" printlist [ tq2 * 2 , l ] = "#" if tq1 + tq2 in [ reduce_map [ ctrl ] * 2 for ctrl in gate . ctrls ]: printlist [ tq1 + tq2 , l ] = "*" + gate . symbol else : printlist [ tq1 + tq2 , l ] = gate . symbol maxlen = max ( maxlen , len ( gate . symbol ) + width ) else : #Multiqubit gate if gate . name == "SWAP" : printlist [ q1 * 2 , l ] = "x" printlist [ q2 * 2 , l ] = "x" else : printlist [ q1 + q2 , l ] = gate . symbol maxlen = max ( maxlen , len ( gate . symbol ) + width ) elif isinstance ( gate , Barrier ): pos = [ i for i in gate . pos if i in reduce_map . keys ()] q1 = reduce_map [ min ( pos )] q2 = reduce_map [ max ( pos )] printlist [ 2 * q1 : 2 * q2 + 1 , l ] = "||" maxlen = max ( maxlen , len ( "||" )) printlist [ - 1 , l ] = maxlen circuitstr = [] for j in range ( 2 * num - 1 ): if j % 2 == 0 : linestr = ( "q[ %d ]" % ( reduce_map_inv [ j // 2 ])) . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), "-" ) for l in range ( depth )]) if reduce_map_inv [ j // 2 ] in self . measures . keys (): linestr += " M->c[ %d ]" % self . measures [ reduce_map_inv [ j // 2 ]] circuitstr . append ( linestr ) else : circuitstr . append ( "" . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), " " ) for l in range ( depth )])) circuitstr = " 
 " . join ( circuitstr ) if return_str : return circuitstr else : print ( circuitstr ) def from_openqasm ( self , openqasm : str ): """ Initialize the circuit from openqasm text. Args: openqasm: input openqasm str. """ from numpy import pi import re self . openqasm = openqasm # lines = self.openqasm.strip("
").splitlines(";") lines = self . openqasm . splitlines () lines = [ line for line in lines if line ] self . gates = [] self . measures = {} measured_qubits = [] global_valid = True for line in lines [ 2 :]: if line : operations_qbs = line . split ( " " , 1 ) operations = operations_qbs [ 0 ] if operations == "qreg" : qbs = operations_qbs [ 1 ] self . num = int ( re . findall ( "\d+" , qbs )[ 0 ]) elif operations == "creg" : pass elif operations == "measure" : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] mb = inds [ 0 ] cb = inds [ 1 ] self . measures [ mb ] = cb measured_qubits . append ( mb ) else : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] valid = True for pos in inds : if pos in measured_qubits : valid = False global_valid = False break if valid : if operations == "barrier" : self . barrier ( inds ) else : sp_op = operations . split ( "(" ) gatename = sp_op [ 0 ] if gatename == "delay" : paras = sp_op [ 1 ] . strip ( "()" ) duration = int ( re . findall ( "\d+" , paras )[ 0 ]) unit = re . findall ( "[a-z]+" , paras )[ 0 ] self . delay ( inds [ 0 ], duration , unit ) elif gatename == "xy" : paras = sp_op [ 1 ] . strip ( "()" ) duration = int ( re . findall ( "\d+" , paras )[ 0 ]) unit = re . findall ( "[a-z]+" , paras )[ 0 ] self . xy ( min ( inds ), max ( inds ), duration , unit ) else : if len ( sp_op ) > 1 : paras = sp_op [ 1 ] . strip ( "()" ) parastr = paras . split ( "," ) paras = [ eval ( parai , { "pi" : pi }) for parai in parastr ] if gatename == "cx" : self . cnot ( inds [ 0 ], inds [ 1 ]) elif gatename == "cy" : self . cy ( inds [ 0 ], inds [ 1 ]) elif gatename == "cz" : self . cz ( inds [ 0 ], inds [ 1 ]) elif gatename == "cp" : self . cp ( inds [ 0 ], inds [ 1 ], paras [ 0 ]) elif gatename == "swap" : self . swap ( inds [ 0 ], inds [ 1 ]) elif gatename == "rx" : self . rx ( inds [ 0 ], paras [ 0 ]) elif gatename == "ry" : self . ry ( inds [ 0 ], paras [ 0 ]) elif gatename == "rz" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "p" : self . p ( inds [ 0 ], paras [ 0 ]) elif gatename == "x" : self . x ( inds [ 0 ]) elif gatename == "y" : self . y ( inds [ 0 ]) elif gatename == "z" : self . z ( inds [ 0 ]) elif gatename == "h" : self . h ( inds [ 0 ]) elif gatename == "id" : self . id ( inds [ 0 ]) elif gatename == "s" : self . s ( inds [ 0 ]) elif gatename == "sdg" : self . sdg ( inds [ 0 ]) elif gatename == "t" : self . t ( inds [ 0 ]) elif gatename == "tdg" : self . tdg ( inds [ 0 ]) elif gatename == "sx" : self . sx ( inds [ 0 ]) elif gatename == "ccx" : self . toffoli ( inds [ 0 ], inds [ 1 ], inds [ 2 ]) elif gatename == "cswap" : self . fredkin ( inds [ 0 ], inds [ 1 ], inds [ 2 ]) elif gatename == "u1" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u2" : self . rz ( inds [ 0 ], paras [ 1 ]) self . ry ( inds [ 0 ], pi / 2 ) self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u3" : self . rz ( inds [ 0 ], paras [ 2 ]) self . ry ( inds [ 0 ], paras [ 0 ]) self . rz ( inds [ 0 ], paras [ 1 ]) elif gatename == "rxx" : self . rxx ( inds [ 0 ], inds [ 1 ], paras [ 0 ]) elif gatename == "ryy" : self . ryy ( inds [ 0 ], inds [ 1 ], paras [ 0 ]) elif gatename == "rzz" : self . rzz ( inds [ 0 ], inds [ 1 ], paras [ 0 ]) else : print ( "Warning: Operations %s may be not supported by QuantumCircuit class currently." % gatename ) if not self . measures : self . measures = dict ( zip ( range ( self . num ), range ( self . num ))) if not global_valid : print ( "Warning: All operations after measurement will be removed for executing on experiment" ) def to_openqasm ( self ) -> str : """ Convert the circuit to openqasm text. Returns: openqasm text. """ qasm = "OPENQASM 2.0; 
 include " qelib1.inc " ; 
 " qasm += "qreg q[ %d ]; 
 " % self . num qasm += "creg meas[ %d ]; 
 " % len ( self . measures ) for gate in self . gates : if isinstance ( gate , SingleQubitGate ): if isinstance ( gate , ParaSingleQubitGate ): if isinstance ( gate . paras , Iterable ): qasm += " %s (" % gate . name . lower () + "," . join ([ " %s " % para for para in gate . paras ]) + ") q[ %d ]; 
