 rx(pi/2) q[ %d ]; 
 rz(pi/4) q[ %d ]; 
 " % ( gate . pos , gate . pos , gate . pos ) else : qasm += " %s q[ %d ]; 
 " % ( gate . name . lower (), gate . pos ) elif isinstance ( gate , Delay ): qasm += "delay( %d%s ) q[ %d ]; 
 " % ( gate . duration , gate . unit , gate . pos ) elif isinstance ( gate , XYResonance ): qasm += "xy( %d%s ) " % ( gate . duration , gate . unit ) + "," . join ([ "q[ %d ]" % p for p in range ( min ( gate . pos ), max ( gate . pos ) + 1 )]) + "; 
 " elif isinstance ( gate , Barrier ) or isinstance ( gate , MultiQubitGate ): if isinstance ( gate , ParaMultiQubitGate ) or ( isinstance ( gate , ControlledGate ) and bool ( gate . paras )): if isinstance ( gate . paras , Iterable ): qasm += " %s (" % gate . name . lower () + "," . join ([ " %s " % para for para in gate . paras ]) + ") " + "," . join ([ "q[ %d ]" % p for p in gate . pos ]) + "; 
