 " % ( key , self . measures [ key ]) self . openqasm = qasm return qasm def id ( self , pos : int ) -> "QuantumCircuit" : """ Identity gate. Args: pos (int): qubit the gate act. """ self . gates . append ( IdGate ( pos )) return self def h ( self , pos : int ) -> "QuantumCircuit" : """ Hadamard gate. Args: pos (int): qubit the gate act. """ self . gates . append ( HGate ( pos )) return self def x ( self , pos : int ) -> "QuantumCircuit" : """ X gate. Args: pos (int): qubit the gate act. """ self . gates . append ( XGate ( pos )) return self def y ( self , pos : int ) -> "QuantumCircuit" : """ Y gate. Args: pos (int): qubit the gate act. """ self . gates . append ( YGate ( pos )) return self def z ( self , pos : int ) -> "QuantumCircuit" : """ Z gate. Args: pos (int): qubit the gate act. """ self . gates . append ( ZGate ( pos )) return self def t ( self , pos : int ) -> "QuantumCircuit" : """ T gate. (~Z^(1/4)) Args: pos (int): qubit the gate act. """ self . gates . append ( TGate ( pos )) return self def tdg ( self , pos : int ) -> "QuantumCircuit" : """ Tdg gate. (Inverse of T gate) Args: pos (int): qubit the gate act. """ self . gates . append ( TdgGate ( pos )) def s ( self , pos : int ) -> "QuantumCircuit" : """ S gate. (~√Z) Args: pos (int): qubit the gate act. """ self . gates . append ( SGate ( pos )) return self def sdg ( self , pos : int ) -> "QuantumCircuit" : """ Sdg gate. (Inverse of S gate) Args: pos (int): qubit the gate act. """ self . gates . append ( SdgGate ( pos )) return self def sx ( self , pos : int ) -> "QuantumCircuit" : """ √X gate. Args: pos (int): qubit the gate act. """ self . gates . append ( SXGate ( pos )) return self def sy ( self , pos : int ) -> "QuantumCircuit" : """ √Y gate. Args: pos (int): qubit the gate act. """ self . gates . append ( SYGate ( pos )) return self def w ( self , pos : int ) -> "QuantumCircuit" : """ W gate. (~(X + Y)/√2) Args: pos (int): qubit the gate act. """ self . gates . append ( WGate ( pos )) return self def sw ( self , pos : int ) -> "QuantumCircuit" : """ √W gate. Args: pos (int): qubit the gate act. """ self . gates . append ( SWGate ( pos )) return self def rx ( self , pos : int , para : float ) -> "QuantumCircuit" : """ Single qubit rotation Rx gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ self . gates . append ( RXGate ( pos , para )) return self def ry ( self , pos : int , para : float ) -> "QuantumCircuit" : """ Single qubit rotation Ry gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ self . gates . append ( RYGate ( pos , para )) return self def rz ( self , pos : int , para : float ) -> "QuantumCircuit" : """ Single qubit rotation Rz gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ self . gates . append ( RZGate ( pos , para )) return self def p ( self , pos : int , para : float ) -> "QuantumCircuit" : """ Phase gate Args: pos (int): qubit the gate act. para (float): rotation angle """ self . gates . append ( PhaseGate ( pos , para )) def cnot ( self , ctrl : int , tar : int ) -> "QuantumCircuit" : """ CNOT gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CXGate ( ctrl , tar )) return self def cy ( self , ctrl : int , tar : int ) -> "QuantumCircuit" : """ Control-Y gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CYGate ( ctrl , tar )) return self def cz ( self , ctrl : int , tar : int ) -> "QuantumCircuit" : """ Control-Z gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CZGate ( ctrl , tar )) return self def cs ( self , ctrl : int , tar : int ) -> "QuantumCircuit" : """ Control-S gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CSGate ( ctrl , tar )) return self def ct ( self , ctrl : int , tar : int ) -> "QuantumCircuit" : """ Control-T gate. Args: ctrl (int): control qubit. tar (int): target qubit. ""