 in range ( abs ( layerj - maxlayer )): gateQlist [ j ] . insert ( pos , None ) maxdepth = max ([ len ( gateQlist [ i ]) for i in range ( num )]) for gates in gateQlist : gates . extend ([ None ] * ( maxdepth - len ( gates ))) for m in self . measures . keys (): if m not in used_qubits : used_qubits . append ( m ) used_qubits = np . sort ( used_qubits ) new_gateQlist = [] for old_qi in range ( len ( gateQlist )): gates = gateQlist [ old_qi ] if old_qi in used_qubits : new_gateQlist . append ( gates ) lc = np . array ( new_gateQlist ) lc = np . vstack (( used_qubits , lc . T )) . T self . circuit = lc self . used_qubits = list ( used_qubits ) return self . circuit def draw_circuit ( self ): """ Draw layered circuit using ASCII, print in terminal. """ self . layered_circuit () gateQlist = self . circuit num = gateQlist . shape [ 0 ] depth = gateQlist . shape [ 1 ] - 1 printlist = np . array ([[ "" ] * depth for i in range ( 2 * num )], dtype = "<U30" ) reduce_map = dict ( zip ( gateQlist [:, 0 ], range ( num ))) reduce_map_inv = dict ( zip ( range ( num ), gateQlist [:, 0 ])) for l in range ( depth ): layergates = gateQlist [:, l + 1 ] maxlen = 3 for i in range ( num ): gate = layergates [ i ] if isinstance ( gate , FixedSingleQubitGate ): printlist [ i * 2 , l ] = gate . name maxlen = max ( maxlen , len ( gate . name ) + 2 ) elif isinstance ( gate , ParaSingleQubitGate ): gatestr = " %s ( %.3f )" % ( gate . name , gate . paras ) printlist [ i * 2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + 2 ) elif isinstance ( gate , FixedTwoQubitGate ): q1 = reduce_map [ min ( gate . pos )] q2 = reduce_map [ max ( gate . pos )] printlist [ 2 * q1 + 1 : 2 * q2 , l ] = "|" if isinstance ( gate , ControlGate ): printlist [ reduce_map [ gate . ctrl ] * 2 , l ] = "*" printlist [ reduce_map [ gate . targ ] * 2 , l ] = "+" maxlen = max ( maxlen , 5 ) if gate . name not in [ "CNOT" , "CX" ]: printlist [ q1 + q2 , l ] = gate . name maxlen = max ( maxlen , len ( gate . name ) + 2 ) else : if gate . name == "SWAP" : printlist [ reduce_map [ gate . pos [ 0 ]] * 2 , l ] = "*" printlist [ reduce_map [ gate . pos [ 1 ]] * 2 , l ] = "*" else : printlist [ reduce_map [ gate . pos [ 0 ]] * 2 , l ] = "#" printlist [ reduce_map [ gate . pos [ 1 ]] * 2 , l ] = "#" printlist [ q1 + q2 , l ] = gate . name maxlen = max ( maxlen , len ( gate . name ) + 2 ) elif isinstance ( gate , ParaTwoQubitGate ): q1 = reduce_map ( min ( gate . pos )) q2 = reduce_map ( max ( gate . pos )) printlist [ 2 * q1 + 1 : 2 * q2 , l ] = "|" printlist [ reduce_map [ gate . pos [ 0 ]] * 2 , l ] = "#" printlist [ reduce_map [ gate . pos [ 1 ]] * 2 , l ] = "#" gatestr = "" if isinstance ( gate . paras , Iterable ): gatestr = ( " %s (" % gate . name + "," . join ( [ " %.3f " % para for para in gate . paras ]) + ")" ) else : gatestr = " %s ( %.3f )" % ( gate . name , gate . paras ) printlist [ q1 + q2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + 2 ) elif isinstance ( gate , Barrier ): pos = [ i for i in gate . pos if i in reduce_map . keys ()] q1 = reduce_map [ min ( pos )] q2 = reduce_map [ max ( pos )] printlist [ 2 * q1 : 2 * q2 + 1 , l ] = "||" maxlen = max ( maxlen , len ( "||" )) printlist [ - 1 , l ] = maxlen circuitstr = [] for j in range ( 2 * num - 1 ): if j % 2 == 0 : linestr = ( "q[ %d ]" % ( reduce_map_inv [ j // 2 ])) . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), "-" ) for l in range ( depth )]) if reduce_map_inv [ j // 2 ] in self . measures . keys (): linestr += " M->c[ %d ]" % self . measures [ reduce_map_inv [ j // 2 ]] circuitstr . append ( linestr ) else : circuitstr . append ( "" . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), " " ) for l in range ( depth )])) circuitstr = " 
 " . join ( circuitstr ) print ( circuitstr ) def from_openqasm ( self , openqasm ): """ Initialize the circuit from openqasm text. """ from numpy import pi import re self . openqasm = openqasm lines = self . openqasm . splitlines () self . gates = [] self . measures = {} measured_qubits = [] global_valid = True for line in lines [ 2 :]: if line : operations_qbs = line . split ( " " , 1 ) operations = operations_qbs [ 0 ] if operations == "qreg" : qbs = operations_qbs [ 1 ] self . num = int ( re . findall ( "\d+" , qbs )[ 0 ]) elif operations == "creg" : pass elif operations == "measure" : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] mb = inds [ 0 ] cb = inds [ 1 ] self . measures [ mb ] = cb measured_qubits . append ( mb ) else : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] valid = True for pos in inds : if pos in measured_qubits : valid = False global_valid = False break if valid : if operations == "barrier" : self . barrier ( inds ) else : sp_op = operations . split ( "(" ) gatename = sp_op [ 0 ] if len ( sp_op ) > 1 : paras = sp_op [ 1 ] . strip ( "()" ) parastr = paras . split ( "," ) paras = [ eval ( parai , { "pi" : pi }) for parai in parastr ] if gatename == "cx" : self . cnot ( inds [ 0 ], inds [ 1 ]) elif gatename == "cy" : self . cy ( inds [ 0 ], inds [ 1 ]) elif gatename == "cz" : self . cz ( inds [ 0 ], inds [ 1 ]) elif gatename == "swap" : self . swap ( inds [ 0 ], inds [ 1 ]) elif gatename == "rx" : self . rx ( inds [ 0 ], paras [ 0 ]) elif gatename == "ry" : self . ry ( inds [ 0 ], paras [ 0 ]) elif gatename == "rz" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "x" : self . x ( inds [ 0 ]) elif gatename == "y" : self . y ( inds [ 0 ]) elif gatename == "z" : self . z ( inds [ 0 ]) elif gatename == "h" : self . h ( inds [ 0 ]) elif gatename == "u1" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u2" : self . rz ( inds [ 0 ], paras [ 1 ]) self . ry ( inds [ 0 ], pi / 2 ) self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u3" : self . rz ( inds [ 0 ], paras [ 2 ]) self . ry ( inds [ 0 ], paras [ 0 ]) self . rz ( inds [ 0 ], paras [ 1 ]) else : print ( "Warning: Operations %s may be not supported by QuantumCircuit class currently." % gatename ) if not self . measures : self . measures = dict ( zip ( range ( self . num ), range ( self . num ))) if not global_valid : print ( "Warning: All operations after measurement will be removed for executing on experiment" ) def to_openqasm ( self ): """ Convert the circuit to openqasm text. Returns: openqasm text. """ qasm = '''OPENQASM 2.0; 
 include "qelib1.inc"; 
 ''' qasm += "qreg q[ %d ]; 
 " % self . num qasm += "creg meas[ %d ]; 
 " % len ( self . measures ) for gate in self . gates : if isinstance ( gate , FixedSingleQubitGate ): qasm += " %s q[ %d ]; 
 " % ( gate . name . lower (), gate . pos ) elif isinstance ( gate , ParaSingleQubitGate ): qasm += " %s ( %s ) q[ %d ]; 
 " % ( gate . name . lower (), gate . paras , gate . pos ) elif isinstance ( gate , FixedTwoQubitGate ): qasm += " %s q[ %d ],q[ %d ]; 
 " % ( gate . name . lower (), gate . pos [ 0 ], gate . pos [ 1 ]) elif type ( gate ) == Barrier : qasm += "barrier " + "," . join ([ "q[ %d ]" % p for p in gate . pos ]) + "; 
 " for key in self . measures : qasm += "measure q[ %d ] -> meas[ %d ]; 
 " % ( key , self . measures [ key ]) self . openqasm = qasm return qasm def h ( self , pos : int ): """ Hadamard gate. Args: pos (int): qubit the gate act. """ self . gates . append ( HGate ( pos )) return self def x ( self , pos : int ): """ X gate. Args: pos (int): qubit the gate act. """ self . gates . append ( XGate ( pos )) return self def y ( self , pos : int ): """ Y gate. Args: pos (int): qubit the gate act. """ self . gates . append ( YGate ( pos )) return self def z ( self , pos : int ): """ Z gate. Args: pos (int): qubit the gate act. """ self . gates . append ( ZGate ( pos )) return self def rx ( self , pos : int , para ): """ Single qubit rotation Rx gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ if para != 0. : self . gates . append ( RXGate ( pos , para )) return self def ry ( self , pos : int , para ): """ Single qubit rotation Ry gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ if para != 0. : self . gates . append ( RYGate ( pos , para )) return self def rz ( self , pos : int , para ): """ Single qubit rotation Rz gate. Args: pos (int): qubit the gate act. para (float): rotation angle """ if para != 0. : self . gates . append ( RZGate ( pos , para )) return self def cnot ( self , ctrl : int , tar : int ): """ CNOT gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CXGate ([ ctrl , tar ])) return self def cy ( self , ctrl : int , tar : int ): """ CY gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CYGate ([ ctrl , tar ])) return self def cz ( self , ctrl : int , tar : int ): """ CZ gate. Args: ctrl (int): control qubit. tar (int): target qubit. """ self . gates . append ( CZGate ([ ctrl , tar ])) return self # def fsim(self, q1, q2, theta, phi): # """ # fSim gate. # Args: # q1, q2 (int): qubits the gate act. # theta (float): parameter theta in fSim. # phi (float): parameter phi in fSim. # """ # self.gates.appen