ot in used_qubits : used_qubits . append ( m ) used_qubits = np . sort ( used_qubits ) new_gateQlist = [] for old_qi in range ( len ( gateQlist )): gates = gateQlist [ old_qi ] if old_qi in used_qubits : new_gateQlist . append ( gates ) lc = np . array ( new_gateQlist ) lc = np . vstack (( used_qubits , lc . T )) . T self . circuit = lc self . used_qubits = list ( used_qubits ) return self . circuit def draw_circuit ( self , width : int = 4 ): """ Draw layered circuit using ASCII, print in terminal. Args: width (int): The width of each gate. """ self . layered_circuit () gateQlist = self . circuit num = gateQlist . shape [ 0 ] depth = gateQlist . shape [ 1 ] - 1 printlist = np . array ([[ "" ] * depth for i in range ( 2 * num )], dtype = "<U30" ) reduce_map = dict ( zip ( gateQlist [:, 0 ], range ( num ))) reduce_map_inv = dict ( zip ( range ( num ), gateQlist [:, 0 ])) for l in range ( depth ): layergates = gateQlist [:, l + 1 ] maxlen = 1 + width for i in range ( num ): gate = layergates [ i ] if isinstance ( gate , FixedSingleQubitGate ): gate_symbol = gate . name if gate . name == "SX" : gate_symbol = "√X" elif gate . name == "SY" : gate_symbol = "√Y" elif gate . name == "SW" : gate_symbol = "√W" printlist [ i * 2 , l ] = gate_symbol maxlen = max ( maxlen , len ( gate_symbol ) + width ) elif isinstance ( gate , ParaSingleQubitGate ): gatestr = " %s ( %.3f )" % ( gate . name , gate . paras ) printlist [ i * 2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + width ) elif isinstance ( gate , Delay ): gatestr = "Delay( %d%s )" % ( gate . duration , gate . unit ) printlist [ i * 2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + width ) elif isinstance ( gate , FixedTwoQubitGate ) or isinstance ( gate , FixedMultiQubitGate ): q1 = reduce_map [ min ( gate . pos )] q2 = reduce_map [ max ( gate . pos )] printlist [ 2 * q1 + 1 : 2 * q2 , l ] = "|" printlist [ reduce_map [ min ( gate . pos )] * 2 , l ] = "#" printlist [ reduce_map [ max ( gate . pos )] * 2 , l ] = "#" if isinstance ( gate , ControlGate ): printlist [ reduce_map [ gate . ctrl ] * 2 , l ] = "*" if gate . targ_name == "X" : targ_symbol = "+" else : targ_symbol = gate . targ_name maxlen = max ( maxlen , len ( targ_symbol ) + width ) printlist [ reduce_map [ gate . targ ] * 2 , l ] = targ_symbol elif gate . name == "SWAP" : printlist [ reduce_map [ gate . pos [ 0 ]] * 2 , l ] = "x" printlist [ reduce_map [ gate . pos [ 1 ]] * 2 , l ] = "x" elif isinstance ( gate , FixedMultiQubitGate ): if hasattr ( gate , "ctrls" ) and hasattr ( gate , "targs" ): for ctrl in gate . ctrls : printlist [ reduce_map [ ctrl ] * 2 , l ] = "*" for ti in range ( len ( gate . targs )): targ = gate . targs [ ti ] if gate . targ_names [ ti ] == "SWAP" : targ_symbol = "x" elif gate . targ_names [ ti ] == "X" : targ_symbol = "+" else : targ_symbol = gate . targ_names [ ti ] maxlen = max ( maxlen , len ( targ_symbol ) + width ) printlist [ reduce_map [ targ ] * 2 , l ] = targ_symbol else : printlist [ q1 + q2 , l ] = gate . name maxlen = max ( maxlen , len ( gate . name ) + width ) else : printlist [ q1 + q2 , l ] = gate . name maxlen = max ( maxlen , len ( gate . name ) + width ) elif isinstance ( gate , ParaTwoQubitGate ) or isinstance ( gate , ParaMultiQubitGate ) or isinstance ( gate , XYResonance ): q1 = reduce_map [ min ( gate . pos )] q2 = reduce_map [ max ( gate . pos )] printlist [ 2 * q1 + 1 : 2 * q2 , l ] = "|" printlist [ reduce_map [ min ( gate . pos )] * 2 , l ] = "#" printlist [ reduce_map [ max ( gate . pos )] * 2 , l ] = "#" gatestr = "" if isinstance ( gate , XYResonance ): gatestr = "XY( %d%s )" % ( gate . duration , gate . unit ) printlist [ q1 + q2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + width ) elif isinstance ( gate , ParaControlGate ): printlist [ reduce_map [ gate . ctrl ] * 2 , l ] = "*" if isinstance ( gate . paras , Iterable ): targ_symbol = ( " %s (" % gate . targ_name + "," . join ( [ " %.3f " % para for para in gate . paras ]) + ")" ) else : targ_symbol = " %s ( %.3f )" % ( gate . targ_name , gate . paras ) maxlen = max ( maxlen , len ( targ_symbol ) + width ) printlist [ reduce_map [ gate . targ ] * 2 , l ] = targ_symbol else : if isinstance ( gate . paras , Iterable ): gatestr = ( " %s (" % gate . name + "," . join ( [ " %.3f " % para for para in gate . paras ]) + ")" ) else : gatestr = " %s ( %.3f )" % ( gate . name , gate . paras ) printlist [ q1 + q2 , l ] = gatestr maxlen = max ( maxlen , len ( gatestr ) + width ) elif isinstance ( gate , Barrier ): pos = [ i for i in gate . pos if i in reduce_map . keys ()] q1 = reduce_map [ min ( pos )] q2 = reduce_map [ max ( pos )] printlist [ 2 * q1 : 2 * q2 + 1 , l ] = "||" maxlen = max ( maxlen , len ( "||" )) printlist [ - 1 , l ] = maxlen circuitstr = [] for j in range ( 2 * num - 1 ): if j % 2 == 0 : linestr = ( "q[ %d ]" % ( reduce_map_inv [ j // 2 ])) . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), "-" ) for l in range ( depth )]) if reduce_map_inv [ j // 2 ] in self . measures . keys (): linestr += " M->c[ %d ]" % self . measures [ reduce_map_inv [ j // 2 ]] circuitstr . append ( linestr ) else : circuitstr . append ( "" . ljust ( 6 ) + "" . join ( [ printlist [ j , l ] . center ( int ( printlist [ - 1 , l ]), " " ) for l in range ( depth )])) circuitstr = " 
 " . join ( circuitstr ) print ( circuitstr ) def from_openqasm ( self , openqasm : str ): """ Initialize the circuit from openqasm text. Args: openqasm: input openqasm str. """ from numpy import pi import re self . openqasm = openqasm # lines = self.openqasm.strip("
").splitlines(";") lines = self . openqasm . splitlines () lines = [ line for line in lines if line ] self . gates = [] self . measures = {} measured_qubits = [] global_valid = True for line in lines [ 2 :]: if line : operations_qbs = line . split ( " " , 1 ) operations = operations_qbs [ 0 ] if operations == "qreg" : qbs = operations_qbs [ 1 ] self . num = int ( re . findall ( "\d+" , qbs )[ 0 ]) elif operations == "creg" : pass elif operations == "measure" : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] mb = inds [ 0 ] cb = inds [ 1 ] self . measures [ mb ] = cb measured_qubits . append ( mb ) else : qbs = operations_qbs [ 1 ] indstr = re . findall ( "\d+" , qbs ) inds = [ int ( indst ) for indst in indstr ] valid = True for pos in inds : if pos in measured_qubits : valid = False global_valid = False break if valid : if operations == "barrier" : self . barrier ( inds ) else : sp_op = operations . split ( "(" ) gatename = sp_op [ 0 ] if gatename == "delay" : paras = sp_op [ 1 ] . strip ( "()" ) duration = int ( re . findall ( "\d+" , paras )[ 0 ]) unit = re . findall ( "[a-z]+" , paras )[ 0 ] self . delay ( inds [ 0 ], duration , unit ) elif gatename == "xy" : paras = sp_op [ 1 ] . strip ( "()" ) duration = int ( re . findall ( "\d+" , paras )[ 0 ]) unit = re . findall ( "[a-z]+" , paras )[ 0 ] self . xy ( min ( inds ), max ( inds ), duration , unit ) else : if len ( sp_op ) > 1 : paras = sp_op [ 1 ] . strip ( "()" ) parastr = paras . split ( "," ) paras = [ eval ( parai , { "pi" : pi }) for parai in parastr ] if gatename == "cx" : self . cnot ( inds [ 0 ], inds [ 1 ]) elif gatename == "cy" : self . cy ( inds [ 0 ], inds [ 1 ]) elif gatename == "cz" : self . cz ( inds [ 0 ], inds [ 1 ]) elif gatename == "cp" : self . cp ( inds [ 0 ], inds [ 1 ], paras [ 0 ]) elif gatename == "swap" : self . swap ( inds [ 0 ], inds [ 1 ]) elif gatename == "rx" : self . rx ( inds [ 0 ], paras [ 0 ]) elif gatename == "ry" : self . ry ( inds [ 0 ], paras [ 0 ]) elif gatename == "rz" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "p" : self . p ( inds [ 0 ], paras [ 0 ]) elif gatename == "x" : self . x ( inds [ 0 ]) elif gatename == "y" : self . y ( inds [ 0 ]) elif gatename == "z" : self . z ( inds [ 0 ]) elif gatename == "h" : self . h ( inds [ 0 ]) elif gatename == "id" : self . id ( inds [ 0 ]) elif gatename == "s" : self . s ( inds [ 0 ]) elif gatename == "sdg" : self . sdg ( inds [ 0 ]) elif gatename == "t" : self . t ( inds [ 0 ]) elif gatename == "tdg" : self . tdg ( inds [ 0 ]) elif gatename == "sx" : self . sx ( inds [ 0 ]) elif gatename == "ccx" : self . toffoli ( inds [ 0 ], inds [ 1 ], inds [ 2 ]) elif gatename == "cswap" : self . fredkin ( inds [ 0 ], inds [ 1 ], inds [ 2 ]) elif gatename == "u1" : self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u2" : self . rz ( inds [ 0 ], paras [ 1 ]) self . ry ( inds [ 0 ], pi / 2 ) self . rz ( inds [ 0 ], paras [ 0 ]) elif gatename == "u3" : self . rz ( inds [ 0 ], paras [ 2 ]) self . ry ( inds [ 0 ], paras [ 0 ]) self . rz ( inds [ 0 ], paras [ 1 ]) else : print ( "Warning: Operations %s may be not supported by QuantumCircuit class currently." % gatename ) if not self . measures : self . measures = dict ( zip ( range ( self . num ), range ( self . num ))) if not global_valid : print ( "Warning: All operations after measurement will be removed for executing on experiment" ) def to_openqasm ( self ) -> str : """ Convert the circuit to openqasm text. Returns: openqasm text. """ qasm = '''OPENQASM 2.0; 
