 " % ( key , self . measures [ key ]) self . openqasm = qasm return qasm def id ( self , pos : int ) -> "QuantumCircuit" : """ Identity gate. Args: pos (int): qubit the gate act. """ self . gates . append ( IdGate ( pos )) return self def h ( self , pos : int ) -> "QuantumCircuit" : """ Hadamard gate. Args: pos (int): qubit the gate act. """ self . gates . append ( HGate ( pos )) return self def x ( self , pos : int ) -> "QuantumCircuit" : """ X gate. Args: pos (int): qubit the gate act. """ self . gates . append ( XGate ( pos )) return self def y ( self , pos : int ) -> "QuantumCircuit" : """ Y gate. Args: pos (int): qubit the gate act. """ self . gates . append ( YGate ( pos )) return self def z ( self , pos : int ) -> "QuantumCircuit" : """ Z gate. Args: pos (int): qubit the gate act. """ self . gates . append ( ZGate ( pos )) return self def t ( self , pos : int ) -> "QuantumCircuit" : """ T gate. (~Z^(1/4)) Args: pos (int): qubit the gate act. """ self . gates . append ( TGate ( pos )) return self def tdg ( self , pos : int ) -> "QuantumCircuit" : """ Tdg gate. (Inverse of T gate) Args: pos (int): qubit the gate act. """ self . gates . append ( TdgGate ( pos )) def s ( self , pos : int ) -> "QuantumCircuit" : """ S gate. (~√Z) Args: pos (int): qubit the gate act. """ self . gates . append ( SGate ( pos )) return self def sdg ( self , pos : int ) -> "QuantumCircuit" : """ Sdg gate. (Inverse of S gate) Args: pos (int): qubit the gate act. """ self . gates . append ( SdgGate ( pos )) return self def sx ( self , pos : int ) -> "QuantumCircuit" : """ √X gate. Args: pos (int): qubit the gate act. """ self . gates . append ( SXGate ( pos )) return self def sy ( self , pos : int ) -> "QuantumCircuit" : """ √Y gate. Args: pos (int): qubit the gate act. """ self . gates . append ( SYGate ( pos )) return self def w ( self , pos : int ) -> "QuantumCircuit" : """ W gate. (~(X + Y)/√2) Args: pos (int): qubit the gate act. """ self . gates . append ( WGate ( pos )) return sel